mapper.select:
SELECT FILE_ID AS 'fileId',
       FILE_SIZE AS 'fileSize',
       FILE_HASH AS 'fileHash',
       PHYSICAL_DELETED_FLAG AS 'physicalDeleteFlag',
       CREATE_TIME AS 'createTime',
       DELETE_FLAG AS 'deleteFlag',
       OPER_ID AS 'operId',
       OPER_TIME AS 'oeprTime'
FROM FILE_INFO


        AND FILE_ID = #{fileId}


        AND FILE_HASH = #{fileHash}


        AND DELETE_FLAG = #{deleteFlag}


        AND PHYSICAL_DELETED_FLAG = #{physicalDeleteFlag}


        AND OPER_TIME >= #{startTime}


        AND OPER_TIME <= #{endTime}
